import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { User } from '../User';
import { CookieService } from 'ngx-cookie-service';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private user: any;

  signUpData = {
    username: '',
    email: '',
    password: '',
    confPassword: '',
  }

  signInData = {
    username: '',
    password: '',
  }

  cookieValue: any;

  constructor(private http: HttpClient, private cookieService: CookieService) {
    this.cookieValue = this.cookieService.get('angularblog');
    console.log(this.cookieValue);
  }

  private headers = new HttpHeaders()
  .set('Content-Type', 'application/json')

  submitSignUp(): Observable<User> {  
    return this.http.post<User>('http://localhost:5000/api/register', this.signUpData, {headers: this.headers, withCredentials: true});
  }

  submitSignIn(): Observable<User> {  
    return this.http.post<User>('http://localhost:5000/api/login', this.signInData, {headers: this.headers, withCredentials: true})
  }



  public getUser(): any {
    // Return user information
    return this.user;
  }

  setUser(user: User) {
    this.user = user;
  }

  public loadUser(): Observable<any> {
    // Fetch user information from server based on JWT token
    const token = localStorage.getItem('angularblog'); // get the token from localStorage
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`); // add token to headers

    return this.http.get<any>('http://localhost:5000/api/verify', { headers });
  }


  public logout(): void {
    // Clear user information and JWT token
    this.user = null;
  }
}
