import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-toggle-form-button',
  templateUrl: './toggle-form-button.component.html',
  styleUrls: ['./toggle-form-button.component.css']
})
export class ToggleFormButtonComponent {
  @Output() toggleFormEvent = new EventEmitter<boolean>();
  showForm = false;

  toggleForm() {
    this.showForm = !this.showForm;
    this.toggleFormEvent.emit(this.showForm);
  }
}
