import { Component } from '@angular/core';
import { BlogsService } from '../../services/blogs.service'

@Component({
  selector: 'app-blog-post-form',
  templateUrl: './blog-post-form.component.html',
  styleUrls: ['./blog-post-form.component.css']
})
export class BlogPostFormComponent {
  showForm = false;
  modalOpen = false;
  formData = {
    title: '',
    content: '',
    postedBy: 'Anonymous',
  }

  constructor(private blogsService: BlogsService) {};

  onFormChange(event: any) {
      const { name, value } = event.target;
      if (name in this.formData) {
        this.formData = { ...this.formData, [name]: value };
      }
  }

  toggleForm(showForm: boolean) {
    this.showForm = !showForm;
  }

  toggleModal() {
    this.modalOpen = !this.modalOpen;
  }

  submitBlog() {
    this.blogsService.submitBlog(
      this.formData.title,
      this.formData.postedBy,
      this.formData.content,
    ).subscribe(response )
  }

}
