import { Component } from '@angular/core';
import { blogs } from 'src/app/mock-blogs';
import { Blog } from 'src/app/Blog';

@Component({
  selector: 'app-blogs',
  templateUrl: './blogs.component.html',
  styleUrls: ['./blogs.component.css']
})
export class BlogsComponent {
  blogs: Blog[];

  constructor() {
    this.blogs = blogs;
    console.log(blogs);
  }
}
<main class="blogs__container" *ngFor="let blog of blogs">
    <div class="blogs__blog">
        <h2>{{blog.title}}</h2>

        <p>{{blog.text}}</p>
    </div>
</main>
