import { Component, Input, Output, EventEmitter } from '@angular/core';
import { BlogsService } from 'src/app/services/blogs.service';

@Component({
  selector: 'app-post-confirmation-modal',
  templateUrl: './post-confirmation-modal.component.html',
  styleUrls: ['./post-confirmation-modal.component.css']
})
export class PostConfirmationModalComponent {

  @Input() isOpen = true;
  @Output() submitClicked = new EventEmitter<void>();

  signUpData = {
    username: '',
    email: '',
    password: ''
  }

  menuSelected = '';
  constructor(private blogsService: BlogsService) {};

  onFormChange(event: any) {
    const { name, value } = event.target;
    if (name in this.signUpData) {
      this.signUpData = { ...this.signUpData, [name]: value.trim() };
    }
    console.log(this.signUpData);
  } 

  onMenuButtonClicked(menu: string) {
    this.menuSelected = menu;
    console.log(this.menuSelected);
  }

  onSubmitClicked() {
    this.submitClicked.emit();
  }

  onSignUpSubmit() {
    this.blogsService.submitSignUp(this.signUpData).subscribe((user) => {
      console.log(user);

    });
  }

  close() {
    this.isOpen = false;
  }
}
<div class="modal" *ngIf="isOpen">
    <div class="modal-content">
        <div class="modal__header">
            <mat-icon class="modal__exit_icon" (click)="close()">close</mat-icon>
        </div>

        <div class="modal__column__container">
            <div class="modal__column__one">
                <h2>Warning!</h2>
                <p>
                    Blogs submitted without signing in expire after three days.
                    Consider signing in before posting your blog to retain your post.
                </p>
                <button class="modal__submit__button" type="submit" (click)="onSubmitClicked()">Submit anonymously</button>
            </div>

            <div class="modal__divider"></div>

            <div class="modal__column__two" *ngIf="menuSelected === ''">
                <h2>Sign In!</h2>
                <p>
                    Your blogs will remain indefinitely.
                </p>
                <div class="modal__column__two__button__container">
                    <button class="modal__submit__button" type="button" (click)="onMenuButtonClicked('signup')">Sign up</button>
                    <button class="modal__submit__button" type='button' (click)="onMenuButtonClicked('signin')">Sign in</button>
                </div>
            </div>

            <form class="modal__column__two" *ngIf="menuSelected === 'signup'" (ngSubmit)="onSignUpSubmit()">
                <h2>Sign Up!</h2>
                <label for="username">
                    Username: <input type="text" name="username" id="username" (change)="onFormChange($event)">
                </label>
                <label for="email">
                    Email: <input type="email" name="email" id="email" (change)="onFormChange($event)">
                </label>
                <label for="password">
                    Password: <input type="password" name="password" id="password" (change)="onFormChange($event)">
                </label>
                <label for="password">
                    Password: <input type="password" name="confPassword" id="password" (change)="onFormChange($event)">
                </label>
                <div class="modal__column__two__button__container">
                    <button class="modal__submit__button" type="button" (click)="onMenuButtonClicked('')">Back</button>
                    <button class="modal__submit__button" type='submit'>Sign up</button>
                </div>
            </form>

            <div class="modal__column__two" *ngIf="menuSelected === 'signin'">
                <h2>Sign In</h2>
                <label for="username">
                    Username or Email: <input type="text" name="username" id="username">
                </label>
                <label for="password">
                    Password: <input type="password" name="password" id="password">
                </label>
                <div class="modal__column__two__button__container">
                    <button class="modal__submit__button" type="button" (click)="onMenuButtonClicked('')">Back</button>
                    <button class="modal__submit__button" type='button' (click)="onMenuButtonClicked('signin')">Sign up</button>
                </div>
            </div>
        </div>
    </div>
</div>
