import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { BlogsService } from '../../services/blogs.service';
import { Blog } from 'src/app/Blog';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-blog-view',
  templateUrl: './blog-view.component.html',
  styleUrls: ['./blog-view.component.css']
})
export class BlogViewComponent implements OnInit {
  blog$: Observable<Blog> = new Observable<Blog>;

  constructor(private route: ActivatedRoute, private blogsService: BlogsService) { }

  ngOnInit(): void {
    const blogId = this.route.snapshot.paramMap.get('blogId') ?? '';
    if (blogId) {
      this.blog$ = this.blogsService.getBlog(blogId);
    }
  }
}
<div *ngIf="blog$ | async as blog" class="blogs__blog">
    <h2>{{blog.title}}</h2>

    <div>
        {{"Posted by: " + blog.postedBy}}
    </div>

    <div>
        {{blog.createdAt}}
    </div>

    <p>{{blog.content}}</p>
</div>
