import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'https://your.api.url'; // Replace with your API URL
  private user: any; // Store user information here

  signUpData = {
    username: '',
    email: '',
    password: '',
    confPassword: '',
  }

  signInData = {
    username: '',
    password: '',
  }

  constructor(private http: HttpClient) { }





  onSignInSubmit() {
      // Couldn't get the post request to actually send without having the .subscribe() call.
    this.blogsService.submitSignIn(this.signInData).subscribe((response => {
      console.log(response);

    }));
    this.close();
  }

  public isAuthenticated(): boolean {
    // Check if user is authenticated based on JWT token presence and validity
    return !!this.getToken();
  }

  public getUser(): any {
    // Return user information
    return this.user;
  }

  public loadUser(): Observable<any> {
    // Fetch user information from server based on JWT token
    const token = this.getToken();
    if (!token) {
      return;
    }
    const options = {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
    return this.http.get(`${this.apiUrl}/user`, options);
  }

  public login(email: string, password: string): Observable<any> {
    // Send login request to server and store user information and JWT token
    const body = {
      email,
      password
    };
    return this.http.post(`${this.apiUrl}/login`, body).pipe(
      tap(response => {
        const { user, token } = response;
        this.user = user;
        document.cookie = `jwt=${token}`;
      })
    );
  }

  public signup(email: string, password: string): Observable<any> {
    // Send signup request to server and store user information and JWT token
    const body = {
      email,
      password
    };
    return this.http.post(`${this.apiUrl}/signup`, body).pipe(
      tap(response => {
        const { user, token } = response;
        this.user = user;
        document.cookie = `jwt=${token}`;
      })
    );
  }

  public logout(): void {
    // Clear user information and JWT token
    this.user = null;
    document.cookie = 'jwt=; expires=Thu, 01 Jan 1970 00:00:01 GMT;';
  }
}
