import { Component } from '@angular/core';
import { BlogsService } from '../../services/blogs.service'
import { AuthService } from 'src/app/services/auth-service.service';
import { Subscription } from 'rxjs'

@Component({
  selector: 'app-blog-post-form',
  templateUrl: './blog-post-form.component.html',
  styleUrls: ['./blog-post-form.component.css']
})
export class BlogPostFormComponent {
  private userSubscription: Subscription;
  showForm = false;
  modalOpen = false;
  formData = {
    title: '',
    content: '',
    postedBy: '',
  }

  constructor(private blogsService: BlogsService, private authService: AuthService) {
    this.userSubscription = this.authService.user$.subscribe(() => {          
      this.setPostedBy();
    });
  };

  onFormChange(event: any) {
      const { name, value } = event.target;
      if (name in this.formData) {
        this.formData = { ...this.formData, [name]: value.trim() };
      }
  }

  setPostedBy() {
      const user = this.authService.getUser();
      if (user) {
        this.formData.postedBy = user;
      } else {
        this.formData.postedBy = 'Anonymous';
      }
  }

  toggleForm(showForm: boolean) {
    this.showForm = !showForm;
  }

  toggleModal() {
    this.modalOpen = !this.modalOpen;
  }

  submitBlog() {    

    this.blogsService.submitBlog(this.formData).subscribe((blog) => {
      this.blogsService.addNewBlog(blog);
    });
  }
}
