import { Component } from '@angular/core';

@Component({
  selector: 'app-blog-post-form',
  templateUrl: './blog-post-form.component.html',
  styleUrls: ['./blog-post-form.component.css']
})
export class BlogPostFormComponent {
  showForm = false;

  toggleForm() {
    this.showForm = !this.showForm;
  }

}
<form class="blogpostform__container">
    <label for="postedBy">
        Alias:
        <input type="text" name="postedBy" id="postedBy" class="blogpostform__titleInput">
    </label>
    <label for="title">
        Title:
        <input type="text" name="title" id="title" class="blogpostform__titleInput">
    </label>

    <textarea name="textInput" id="textInput" class="blogpostform__textInput" placeholder="Your blog's text..."></textarea>

    <button class="blogpostform__button">Submit</button>
</form>
