import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'
import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { HeaderComponent } from './components/header/header.component';
import { SearchBarComponent } from './components/search-bar/search-bar.component';
import { BlogsComponent } from './components/blogs/blogs.component';
import { HttpClientModule } from '@angular/common/http';
import { BlogPostFormComponent } from './components/blog-post-form/blog-post-form.component';
import { MatIconModule } from '@angular/material/icon';
import { PostConfirmationModalComponent } from './components/post-confirmation-modal/post-confirmation-modal.component';
import { JwtModule } from "@auth0/angular-jwt";

export function tokenGetter() {
  return document.cookie.split(';')
    .map(c => c.trim())
    .filter(c => c.startsWith('angularblog='))
    .map(c => c.substring('angularblog='.length, c.length))
    .shift();
}

@NgModule({
  declarations: [
    AppComponent,
    HeaderComponent,
    SearchBarComponent,
    BlogsComponent,
    BlogPostFormComponent,
    PostConfirmationModalComponent,
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    FormsModule,
    HttpClientModule,
    MatIconModule,
    JwtModule.forRoot({
      config: {
        tokenGetter: tokenGetter,
        allowedDomains: ["localhost:"],
        disallowedRoutes: ["http://example.com/examplebadroute/"],
      },
    }),
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
