import { Component } from '@angular/core';

@Component({
  selector: 'app-blog-post-form',
  templateUrl: './blog-post-form.component.html',
  styleUrls: ['./blog-post-form.component.css']
})
export class BlogPostFormComponent {

}
<form class="blogpostform__container">
    <label for="postedBy">
        Alias:
        <input type="text" name="postedBy" id="postedBy" class="blogpostform__textInput">
    </label>
    <label for="title">
        Title:
        <input type="text" name="title" id="title" class="blogpostform__textInput">
    </label>

    <textarea name="textInput" id="textInput" class="textInput"></textarea>
</form>
