import { Component } from '@angular/core';
import { blogs } from 'src/app/mock-blogs';
import { Blog } from 'src/app/Blog';

@Component({
  selector: 'app-blogs',
  templateUrl: './blogs.component.html',
  styleUrls: ['./blogs.component.css']
})
export class BlogsComponent {
  blogs: Blog[];
  leftColumn: Blog[];
  rightColumn: Blog[];

  constructor() {
    this.blogs = blogs;
    this.leftColumn = this.blogs.filter((_, index) => index % 2 === 0);
    this.rightColumn = this.blogs.filter((_, index) => index % 1 === 0);
    console.log(blogs);
  }
}
<main class="blogs__container" >
    <div class="blogs__column" *ngFor="let blog of leftColumn">
    <div class="blogs__blog">
            <h2>{{blog.title}}</h2>

            <div>
                {{blog.timestamp}}
            </div>

            <p>{{blog.text}}</p>
        </div>
    </div>

    <div class="blogs__column" *ngFor="let blog of rightColumn">
        <div class="blogs__blog">
                <h2>{{blog.title}}</h2>

                <div>
                    {{blog.timestamp}}
                </div>

                <p>{{blog.text}}</p>
            </div>
    </div>
</main>
