import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-search-bar',
  templateUrl: './search-bar.component.html',
  styleUrls: ['./search-bar.component.css']
})
export class SearchBarComponent {
  @Output searchObjectUpdated = 
  searchObject: {
    selectedOption: string,
    searchQuery: string;
  };

  constructor() {
    this.searchObject = { selectedOption: 'title', searchQuery: ''};
  }

  options = [
    { value: 'title', label: "Title"},
    { value: 'category', label: "Category"},
    { value: 'user', label: 'User'}
  ]

  onSelect(option: string) {
    this.searchObject.selectedOption = option;
    console.log('Selected option: ', this.searchObject.selectedOption);
  }

  onSearchChange(event: any) {
    this.searchObject.searchQuery = event.target.value;
    console.log('Search query: ', this.searchObject.searchQuery);
  }
}
