import { Component, Input, Output, EventEmitter } from '@angular/core';
import { AuthService } from 'src/app/services/auth-service.service';
import { blogs } from 'src/app/'

@Component({
  selector: 'app-post-confirmation-modal',
  templateUrl: './post-confirmation-modal.component.html',
  styleUrls: ['./post-confirmation-modal.component.css']
})
export class PostConfirmationModalComponent {

  @Input() isOpen = true;
  @Output() submitClicked = new EventEmitter<void>();

  menuSelected = '';
  constructor(private authService: AuthService, private blogService: BlogService) {}


  onMenuButtonClicked(menu: string) {
    this.menuSelected = menu;
  }

  onSubmitClicked() {
    this.submitClicked.emit();
    this.close();
  }

  close() {
    this.isOpen = false;
  }

  onSignUpFormChange(event: any) {
    const { name, value } = event.target;
    if (name in this.authService.signUpData) {
      this.authService.signUpData = { ...this.authService.signUpData, [name]: value.trim() };
    }
  }

  onSignInFormChange(event: any) {
    const { name, value } = event.target;
    if (name in this.authService.signInData) {
      this.authService.signInData = { ...this.authService.signInData, [name]: value.trim() };
    }
  }

  onSignUp() {
    this.authService.submitSignUp().subscribe(user => {
      if (user) {
        this.close();
      }
      this.authService.setUser(user);
    })
  }

  onSignIn() {
    this.authService.submitSignIn().subscribe(user => {     
      if (user) {
        this.close();
      }

      this.authService.setUser(user);
    })
  }
}
